// src/App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [contentList, setContentList, setData] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [mockFileLink, setMockFileLink] = useState('');

  // useEffect(() => {
  //   axios.get('/api/content')
  // .then(response => console.log('Backend response:', response.data))
  // .catch(error => console.error('Backend error:', error));

  // },[]);

  // Temporary function call without useEffect
const fetchData = async () => {
  try {
    const response = await axios.get('/api/content');
    // handle the response
    setData(response.data); 
  } catch (error) {
    console.error('Backend error:', error.response.data);
  }
};

fetchData(); // Call the function


  const handleSubmit = () => {
    axios
      .post('/api/content', { title, description, mockFileLink })
      .then((response) => {
        setContentList([...contentList, response.data.content]);
        setTitle('');
        setDescription('');
        setMockFileLink('');
      })
      .catch((error) => {
        console.error('Error submitting content:', error);
      });
  };

  return (
    <div>
      <h1>Content Management System</h1>
      <div>
        <h2>Submit Content</h2>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <label>Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <label>Mock File Link:</label>
        <input
          type="text"
          value={mockFileLink}
          onChange={(e) => setMockFileLink(e.target.value)}
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>
      <div>
        <h2>Content List</h2>
        <ul>
          {contentList.map((content) => (
            <li key={content._id}>
              <strong>{content.title}</strong>: {content.description}
              <br />
              <a href={content.mockFileLink} target="_blank" rel="noopener noreferrer">
                View Mock File
              </a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
