import React, { useState } from 'react';
import axios from 'axios';

const ContentForm = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [mockFileLink, setMockFileLink] = useState('');
  const [error, setError] = useState('');

  // frontend/src/components/ContentForm.js
const handleSubmit = async (e) => {
  e.preventDefault();

  // Basic form validation
  if (!title || !description || !mockFileLink) {
    setError('All fields are required');
    return;
  }

  try {
    const response = await axios.post('http://localhost:5000/api/content', { title, description, mockFileLink });
    console.log('Content submitted successfully:', response.data);

    // Reset form fields and error state on successful submission
    setTitle('');
    setDescription('');
    setMockFileLink('');
    setError('');
  } catch (error) {
    console.error('Error submitting content:', error.response.data);
    setError('Error submitting content. Please try again.');
  }
};


  return (
    <form onSubmit={handleSubmit}>
      <label>Title:</label>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />

      <label>Description:</label>
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} />

      <label>Mock File Link:</label>
      <input type="text" value={mockFileLink} onChange={(e) => setMockFileLink(e.target.value)} />

      <button type="submit">Submit</button>
    </form>
  );
};

export default ContentForm;
